import React, { useState, useEffect } from 'react';
import { Button, Modal, Select, Row, Col, Descriptions, Image, message } from 'antd';
import { createCommissionSettlement, website } from '../api/api';
import { NO } from '../utils/id';
import { formatDate } from '../utils/date';

const { Option } = Select;
function SettlementViewModel(props) {
    const onClose = e => {
        props.closeModel();
    }
    return (
        <Modal
            title="结算详情"
            visible={props.visible}
            footer={null}
            onCancel={onClose}
        >   <div style={{ height: "100%", width: "100%" }}>
                <Descriptions bordered>
                    <Descriptions.Item label="起止时间" span={3}>
                        {props.record && formatDate(new Date(Date.parse(props.record.start_at)))}
                    </Descriptions.Item>
                    <Descriptions.Item label="截至" span={3}>
                        {props.record && formatDate(new Date(Date.parse(props.record.end_at)))}
                    </Descriptions.Item>
                    <Descriptions.Item label="操作人" span={3}>
                        {props.record && props.record.author_name}
                    </Descriptions.Item>
                    <Descriptions.Item label="销售额" span={3}>
                        ￥{props.record && props.record.amount}
                    </Descriptions.Item>
                    <Descriptions.Item label="佣金" span={3}>
                        ￥{props.record && props.record.commission}
                    </Descriptions.Item>
                    <Descriptions.Item label="票据" span={3}>
                        {props.record && <Image
                            width={100}
                            src={website + "/data/org_attach/" + props.record.invoice}
                            fallback="data:image/png;base64,"
                        />}

                    </Descriptions.Item>
                    <Descriptions.Item label="成交订单" span={3}>
                        {props.record && props.record.success_orders != null && props.record.success_orders.map((v) =>
                            <div>{NO("PM", v)}</div>
                        )}
                    </Descriptions.Item>
                    <Descriptions.Item label="退费订单" span={3}>
                        {props.record && props.record.failed_orders != null && props.record.failed_orders.map((v) =>
                            <div>{NO("PM", v)}</div>
                        )}
                    </Descriptions.Item>
                </Descriptions>

                <Row justify="end" style={{ marginTop: 10 }}>
                    <Col >
                        <Button htmlType="button" onClick={onClose}>
                            返回
                        </Button>
                    </Col>
                </Row>
            </div>
        </Modal >
    );
}

export default SettlementViewModel;
